","type":"uint256"}],"stateMutability":"view","type":"function"},{"inputs":[{"internalType":"uint256","name":"surveyNumber","type":"uint256"}],"name":"landInfoOwner","outputs":[{"internalType":"string","name":"","type":"string"},{"internalType":"string","name":"","type":"string"},{"internalType":"string","name":"","type":"string"},{"internalType":"uint256","name":"","type":"uint256"},{"internalType":"bool","name":"","type":"bool"},{"internalType":"address","name":"","type":"address"},{"internalType":"enum TitleRegistry.ReqStatus","name":"","type":"uint8"}],"stateMutability":"view","type":"function"},{"inputs":[{"internalType":"uint256","name":"surveyNumber","type":"uint256"}],"name":"landInfoUser","outputs":[{"internalType":"address","name":"","type":"address"},{"internalType":"uint256","name":"","type":"uint256"},{"internalType":"bool","name":"","type":"bool"},{"internalType":"address","name":"","type":"address"},{"internalType":"enum TitleRegistry.ReqStatus","name":"","type":"uint8"}],"stateMutability":"view","type":"function"},{"inputs":[{"internalType":"uint256","name":"surveyNumber","type":"uint256"}],"name":"makeAvailable","outputs":[],"stateMutability":"nonpayable","type":"function"},{"inputs":[{"internalType":"uint256","name":"surveyNumber","type":"uint256"}],"name":"makeUnavailable","outputs":[],"stateMutability":"nonpayable","type":"function"},{"inputs":[{"internalType":"uint256","name":"surveyNumber","type":"uint256"},{"internalType":"enum TitleRegistry.ReqStatus","name":"status","type":"uint8"}],"name":"processRequest","outputs":[],"stateMutability":"nonpayable","type":"function"},{"inputs":[{"internalType":"string","name":"_state","type":"string"},{"internalType":"string","name":"_district","type":"string"},{"internalType":"string","name":"_neighborhood","type":"string"},{"internalType":"uint256","name":"_surveyNumber","type":"uint256"},{"internalType":"address payable","name":"_ownerAddress","type":"address"},{"internalType":"uint256","name":"_marketVa